    private void alterarDadosActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_alterarDadosActionPerformed
        // botao alterar dados 
       
         try {
    
            if(campoNome.getText().length() == 0 && campoEmail.getText().length() == 0 &&  new String (campoSenha.getPassword()).length() == 0) {
            JOptionPane.showMessageDialog(null, "Erro: Todos os campos est√£o vazios!");
            return; 
            }

            if(campoNome.getText().length() != 0) new ConnectDAO().AlterName(idUser, campoNome.getText());
            if(campoEmail.getText().length() != 0) new ConnectDAO().AlterEmail(idUser, campoEmail.getText());
            if( new String (campoSenha.getPassword()).length() != 0) new ConnectDAO().AlterSenha(idUser, new String (campoSenha.getPassword()));

    
            JOptionPane.showMessageDialog(null, "Dados alterados com sucesso!");
            new Login().setVisible(true);
            dispose();
            
            } catch (HeadlessException e) {
    
                    JOptionPane.showMessageDialog(null, "Ocorreu um erro ao alterar os dados: " + e.getMessage());
            }

    }//GEN-LAST:event_alterarDadosActionPerformed

