    private void criarListaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_criarListaActionPerformed
        // botao Criar Lista
        
        Flashcards flashcards = new Flashcards();
        flashcards.setIdUser(idUser);
        flashcards.setNameList(campoNome.getText());
        
        
        if(new ConnectDAO().CheckList(flashcards) == true){
            JOptionPane.showMessageDialog(null, "Essa lista j√° existe", "Aviso", 2);
        }
        else{
            int i =  new ConnectDAO().InsertListName(flashcards);
            JOptionPane.showMessageDialog(null, "Lista criada com sucesso!", "Aviso", 1);
            //new CriarCards(i,idUser, flashcards.getNameList()).setVisible(true);
            new EditarCards(flashcards,(String)this.campoNome.getText(),idUser).setVisible(true);
            dispose();
            
        }
        
    }//GEN-LAST:event_criarListaActionPerformed

